// src/js/Phase10.js
import React, { useState } from 'react';
import clues from '../data/clues'; // Importar las pistas

const Phase10 = ({ nextPhase, hintUsed }) => {
  const [answer, setAnswer] = useState('');
  const clue = clues[9]; // Acceder a la pista correspondiente a la fase 10

  const checkAnswer = () => {
    if (answer.toLowerCase() === clue.answer.toLowerCase()) {
      // Aquí puedes agregar la lógica para mostrar el video al final
      alert('¡Felicidades! Has completado el juego.');
    } else {
      alert('Respuesta incorrecta. Intenta de nuevo.');
    }
  };

  return (
    <div>
      <h2>{clue.question}</h2>
      <input 
        type="text" 
        value={answer} 
        onChange={(e) => setAnswer(e.target.value)} 
        placeholder="Escribe tu respuesta"
      />
      <button onClick={checkAnswer}>Enviar</button>
      {hintUsed && <p>Pista: {clue.hint}</p>} {/* Muestra la pista si fue usada */}
    </div>
  );
};

export default Phase10;
